// 定义地支数组
const DIZHI = ['子', '丑', '寅', '卯', '辰', '巳', '午', '未', '申', '酉', '戌', '亥'];

// 定义天德地支关系
const TIANDE = {
  '子': '辰',
  '丑': '巳',
  '寅': '午',
  '卯': '未',
  '辰': '申',
  '巳': '酉',
  '午': '戌',
  '未': '亥',
  '申': '子',
  '酉': '丑',
  '戌': '寅',
  '亥': '卯'
};

// 定义月德地支关系
const YUEDE = {
  '子': '巳',
  '丑': '午',
  '寅': '未',
  '卯': '申',
  '辰': '酉',
  '巳': '戌',
  '午': '亥',
  '未': '子',
  '申': '丑',
  '酉': '寅',
  '戌': '卯',
  '亥': '辰'
};

// 定义福星地支关系
const FUXING = {
  '子': '巳',
  '丑': '午',
  '寅': '未',
  '卯': '申',
  '辰': '酉',
  '巳': '戌',
  '午': '亥',
  '未': '子',
  '申': '丑',
  '酉': '寅',
  '戌': '卯',
  '亥': '辰'
};

// 定义贵人地支关系
const GUIREN = {
  '子': '丑',
  '丑': '子',
  '寅': '未',
  '卯': '申',
  '辰': '酉',
  '巳': '戌',
  '午': '亥',
  '未': '寅',
  '申': '卯',
  '酉': '辰',
  '戌': '巳',
  '亥': '午'
};

// 定义童子煞地支
const TONGZISHA = ['子', '午', '卯', '酉'];

/**
 * 判断是否有华盖
 * @param {string} yearDizhi - 年柱地支
 * @param {string} dayDizhi - 日柱地支
 * @returns {boolean} - 是否有华盖
 */
function hasHuaGai(yearDizhi, dayDizhi) {
  const huagaiDizhi = ['巳', '午', '未', '申'];
  return huagaiDizhi.includes(yearDizhi) || huagaiDizhi.includes(dayDizhi);
}

/**
 * 判断是否有天德
 * @param {string} yearDizhi - 年柱地支
 * @param {string} dayDizhi - 日柱地支
 * @returns {boolean} - 是否有天德
 */
function hasTianDe(yearDizhi, dayDizhi) {
  return TIANDE[yearDizhi] === dayDizhi;
}

/**
 * 判断是否有月德
 * @param {string} monthDizhi - 月柱地支
 * @param {string} dayDizhi - 日柱地支
 * @returns {boolean} - 是否有月德
 */
function hasYueDe(monthDizhi, dayDizhi) {
  return YUEDE[monthDizhi] === dayDizhi;
}

/**
 * 判断是否有福星
 * @param {string} yearDizhi - 年柱地支
 * @param {string} dayDizhi - 日柱地支
 * @returns {boolean} - 是否有福星
 */
function hasFuXing(yearDizhi, dayDizhi) {
  return FUXING[yearDizhi] === dayDizhi;
}

/**
 * 判断是否有贵人
 * @param {string} hourDizhi - 时柱地支
 * @param {string} dayDizhi - 日柱地支
 * @returns {boolean} - 是否有贵人
 */
function hasGuiRen(hourDizhi, dayDizhi) {
  return GUIREN[hourDizhi] === dayDizhi;
}

/**
 * 判断是否有童子煞
 * @param {string} yearDizhi - 年柱地支
 * @param {string} dayDizhi - 日柱地支
 * @returns {boolean} - 是否有童子煞
 */
function hasTongZiSha(yearDizhi, dayDizhi) {
  return TONGZISHA.includes(yearDizhi) || TONGZISHA.includes(dayDizhi);
}
